from sys import version_info
if version_info >= (2, 6, 0):
    def swig_import_helper():
        from os.path import dirname
        import imp
        fp = None
        try:
            fp, pathname, description = imp.find_module('_loss', [dirname(__file__)])
        except ImportError:
            import _loss
            return _loss
        if fp is not None:
            try:
                _mod = imp.load_module('_loss', fp, pathname, description)
            finally:
                fp.close()
            return _mod
    _loss = swig_import_helper()
    del swig_import_helper
else:
    import _loss
del version_info
